$(document).ready(function(){$(".navbar-burger").click(function(){$(".navbar-burger").toggleClass("is-active");$(".navbar-menu").toggleClass("is-active")});$(".navbar-item.has-dropdown").click(function(){$(".navbar-burger").is(":visible")&&$(this).toggleClass("is-active")});$(".navbar-item > .navbar-link").click(function(n){$(".navbar-burger").is(":visible")&&n.preventDefault()});$(window).resize(function(){!$(".navbar-burger").is(":visible")&&$(".navbar-item.has-dropdown.is-active").length&&$(".navbar-item.has-dropdown.is-active").removeClass("is-active")})});